import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./AddProductsStyle.css";
import { useHistory } from "react-router-dom";

const AddProducts = () => {
  const [passShow, setPassShow] = useState(false);
  const [cpassShow, setCPassShow] = useState(false);
  const [isCheck, setIsCheck] = useState(false);

  const [inpval, setInpval] = useState({
    brandName: "",
    name: "",
    description: "",
    price: "",
    category: "",
    imageUrl: "",
  });
  console.log();

  const history = useHistory();

  const setVal = (e) => {
    // console.log(e.target.value);
    const { name, value } = e.target;

    setInpval(() => {
      return {
        ...inpval,
        [name]: value,
      };
    });
  };

  const addProduct = async (e) => {
    e.preventDefault();
    // if (!isCheck) return;

    const { name, decsription, price, imageUrl, brandName, category } = inpval;

    if (name === "") {
      toast.warning("name is required!", {
        position: "top-center",
      });
    } else if (decsription === "") {
      toast.error("decsription is required!", {
        position: "top-center",
      });
    } else if (price === "") {
      toast.error("price is required!", {
        position: "top-center",
      });
    } else if (imageUrl === "") {
      toast.error("imageUrl is required!", {
        position: "top-center",
      });
    } else if (brandName === "") {
      toast.error("imageUrl is required!", {
        position: "top-center",
      });
    } else if (category === "") {
      toast.error("imageUrl is required!", {
        position: "top-center",
      });
    } else {
      // console.log("user registration succesfully done");

      const data = await fetch("http://localhost:8001/api/product/addProduct", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          decsription,
          price,
          brandName,
          stock: 12,
          category,
        }),
      });

      const res = await data.json();
      console.log(res.status);

      if (res.status === "Status Success") {
        history.push("/SignIn");
        toast.success("Product Registered SuccessFully ðŸ˜ƒ!", {
          position: "top-center",
        });
        setInpval({
          ...inpval,
          name: "",
          decsription: "",
          price: "",
          imageUrl: "",
          category: "",
        });
      }
    }
  };
  return (
    <>
      <section>
        <div className="form_data">
          <div className="form_heading">
            <h1> Sell Your Properties Now on Good Rates</h1>
            <p style={{ textAlign: "center" }}>Add New Sell Posts</p>
          </div>

          <form>
            {/* Name */}
            <div className="form_input">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                onChange={setVal}
                value={inpval.name}
                name="name"
                id="name"
                placeholder="Enter Your Name"
              />
            </div>
            {/* decsription */}
            <div className="form_input">
              <label htmlFor="decsription">decsription</label>
              <input
                type="text"
                onChange={setVal}
                value={inpval.description}
                name="decsription"
                id="decsription"
                placeholder="Enter Your Product decsription"
              />
            </div>
            {/* Category */}
            <div className="form_input">
              <label htmlFor="category">Category</label>
              <input
                type="text"
                onChange={setVal}
                value={inpval.category}
                name="category"
                id="category"
                placeholder="Category"
              />
            </div>
            {/* Price */}
            <div className="form_input">
              <label htmlFor="price">price</label>
              <div className="two">
                <input
                  type="number"
                  value={inpval.price}
                  onChange={setVal}
                  name="price"
                  id="price"
                  placeholder="Enter Your price"
                />
              </div>
            </div>
            {/* ImageURL */}
            <div className="form_input">
              <label htmlFor="price"> Enter Your image Url</label>
              <div className="two">
                <input
                  value={inpval.imageUrl}
                  onChange={setVal}
                  name="imageUrl"
                  id="imageUrl"
                  placeholder=" Add Image URL"
                />
              </div>
            </div>
            {/* BrandName */}
            <div className="form_input">
              <label htmlFor="brandName"> Enter Your BrandName</label>
              <div className="two">
                <input
                  value={inpval.brandName}
                  onChange={setVal}
                  name="brandName"
                  id="brandName"
                  placeholder=" Add Your Brand Name"
                />
              </div>
            </div>

            <button className="btn" onClick={addProduct}>
              Create Product
            </button>
          </form>
          <ToastContainer />
        </div>
      </section>
    </>
  );
};

export default AddProducts;
